function createSiteEvent(s_name, s_placeId, s_location, s_url, s_vinicity,
e_name, e_cost, e_description, e_url, eb_eventId, 
e_type, e_category, e_typelist, e_photoUrl) {
	var gr = new GlideRecord('x_snc_guide_site');
	gr.initialize();
	gr.description = 'Created by: Run_me_first fixscript';
	gr.eventbrite_created = 'true';
	gr.name = s_name;
	gr.g_name = gr.name;
	gr.g_place_id = s_placeId;
	gr.g_location = s_location;
	gr.g_site_google_url = s_url;
	gr.g_vicinity = s_vinicity;
	gr.location = gr.g_vicinity;
	gr.insert();

	var ev = new GlideRecord('x_snc_guide_event');
	ev.initialize();
	ev.site = gr.sys_id;
	ev.active = true;
	ev.state = 'Draft';
	ev.start_date = getRandomDate();
	ev.end_date = getRandomEndDate(ev.start_date);
	ev.name = e_name;
	ev.cost = e_cost;
	ev.description = e_description;
	ev.url = e_url;
	ev.eb_event_id = eb_eventId;
	ev.type = e_type;
	//loadCatTypeList(ev.type, ev);
	ev.eb_category_id = e_category;
	ev.type_list = e_typelist;
	ev.photo_url = e_photoUrl;
	ev.insert();
}

//
// returns a random date.  if less than june ups the year.
//
function getRandomDate() {
	var month = getRandomInt(1,12);
	var day = getRandomInt(1,28);
	var year = '2017';
	if (month<6) {
		year = '2018';
	}
	mdate = month + '-' + day + '-' + year;
	return mdate;
}

//
// returns a random end date
//
function getRandomEndDate(startDt) {
	var gdt = new GlideDateTime(startDt);
	var days = getRandomInt(1, 7);
	gdt.addDaysLocalTime(days);
	var end_date = gdt.getDate();
	return end_date;

}

//
// Returns a random integer between min (included) and max (included)
//
function getRandomInt(min, max) {
	return Math.floor(Math.random() * (max - min + 1)) + min;
}
